import React from "react";
import { View, Text, FlatList } from "react-native";

export default class ScrollableList extends React.Component {
  render() {
    return (
      <FlatList
        horizontal={true}
        data={trendsArray}
        showsHorizontalScrollIndicator={false}
        renderItem={(trends) => {
          console.log(trends);
          return (
            <Text
              style={{ marginRight: 12, marginTop: 8, color: "gray" }}
              key={trends}
            >
              {trends.item}
            </Text>
          );
        }}
      ></FlatList>
    );
  }
}
