import { StatusBar } from "expo-status-bar";
import React from "react";
import { StyleSheet, Text, View, ListView, FlatList } from "react-native";
import AppBar from "./AppBar";
import GlobalFont from "./globalFont";

var trendsArray = {
  data: "# COVID19",
  data: "# IPL2020",
  data: "# CORONA",
  data: "# NEET",
  data: "# GLOBAL CRISIS",
};

export default class App extends React.Component {
  render() {
    return (
      <View style={styles.main}>
        <AppBar></AppBar>
        <View>
          <GlobalFont style={[styles.title]}>Trends</GlobalFont>
          <ScrollableList></ScrollableList>
        </View>
      </View>
    );
  }
}

class ScrollableList extends React.Component {
  render() {
    return (
      <FlatList
        horizontal={true}
        data={trendsArray}
        renderItem={(trends) => {
          console.log(trends.data);
          return (
            <Text style={{ marginRight: 8, color: "gray" }} key={trends}>
              {trends}
            </Text>
          );
        }}
      ></FlatList>
    );
  }
}

const styles = StyleSheet.create({
  main: {
    marginHorizontal: 15,
  },
  title: {
    fontSize: 15,
    fontWeight: "600",
  },
  scrollList: {
    overflow: "scroll",
    paddingVertical: 10,
  },
  hotTopics: { fontSize: 18, marginRight: 8, color: "gray" },
});
