import { StatusBar } from "expo-status-bar";
import React from "react";
import { StyleSheet, Text, View, ScrollView } from "react-native";
import AppBar from "./AppBar";
import { Font } from "expo";

export default class App extends React.Component {
  state = {
    fontsLoaded: false,
  };

  async componentDidMount() {
    await Font.loadAsync({
      //font1 or 2 can be any name. This'll be used in font-family

      font1: require("../assets/fonts/font1.ttf"),
    });
    this.setState({ fontsLoaded: true });
  }

  render() {
    if (this.state.fontsLoaded) {
      return (
        <View style={styles.main}>
          <AppBar></AppBar>
          <View>
            <Text style={styles.title}>Trends</Text>
            <ScrollableList></ScrollableList>
          </View>
        </View>
      );
    } else {
      return (
        <View>
          <Text>Loading...</Text>
        </View>
      );
    }
  }
}

class ScrollableList extends React.Component {
  render() {
    return (
      <ScrollView
        style={styles.scrollList}
        horizontal={true}
        showsHorizontalScrollIndicator={false}
      >
        <Text style={{ marginRight: 8, color: "gray" }}># COVID19</Text>
        <Text style={{ marginHorizontal: 8, color: "gray" }}># IPL2020</Text>
        <Text style={{ marginHorizontal: 8, color: "gray" }}>
          # CoronaVaccine
        </Text>
        <Text style={{ marginHorizontal: 8, color: "gray" }}># NEET</Text>
        <Text style={{ marginLeft: 8, color: "gray" }}># Trump</Text>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  main: {
    marginHorizontal: 15,
  },
  title: {
    fontSize: 20,
    fontWeight: "600",
  },
  scrollList: {
    overflow: "scroll",
    paddingVertical: 10,
  },
  hotTopics: { fontSize: 18, marginRight: 8, color: "gray" },
});
