import { StatusBar } from "expo-status-bar";
import React from "react";
import { StyleSheet, Text, View, ScrollView } from "react-native";
import AppBar from "./AppBar";
import * as Font from "expo-font";

export default class App extends React.Component {
  constructor() {
    super();
    this.state = {
      fontsLoaded: false,
    };
  }

  async componentDidMount() {
    try {
      await Font.loadAsync({
        // font1: require("./assets/fonts/font1.ttf"),
        "Inter-SemiBoldItalic":
          "https://rsms.me/inter/font-files/Inter-SemiBoldItalic.otf?v=3.12",
      });

      this.setState({ fontLoaded: true });
      // alert("fonts are loaded");
    } catch (error) {
      // alert(error);
    }
  }

  render() {
    return (
        <>
            {this.state.fontLoaded ? ( 
                    <Button style={{marginTop:20, fontFamily: "Ranchers"}} icon="box" mode="contained" onPress={() => alert('Pressed')}>
                        Click me
                    </Button>
            ) : (
                 <AppLoading />
            )}

        </>

    );
}
}

class ScrollableList extends React.Component {
  render() {
    return (
      <ScrollView
        style={styles.scrollList}
        horizontal={true}
        showsHorizontalScrollIndicator={false}
      >
        <Text style={{ marginRight: 8, color: "gray" }}># COVID19</Text>
        <Text style={{ marginHorizontal: 8, color: "gray" }}># IPL2020</Text>
        <Text style={{ marginHorizontal: 8, color: "gray" }}>
          # CoronaVaccine
        </Text>
        <Text style={{ marginHorizontal: 8, color: "gray" }}># NEET</Text>
        <Text style={{ marginLeft: 8, color: "gray" }}># Trump</Text>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  main: {
    marginHorizontal: 15,
  },
  title: {
    fontSize: 20,
    fontWeight: "600",
  },
  scrollList: {
    overflow: "scroll",
    paddingVertical: 10,
  },
  hotTopics: { fontSize: 18, marginRight: 8, color: "gray" },
});
