import React from "react";
import { Text, FlatList } from "react-native";

export default class ScrollableList extends React.Component {
  state = {
    trendsArray: [
      "# COVID19",
      "# IPL2020",
      "# CORONA",
      "# NEET",
      "# GLOBAL CRISIS",
    ],
  };

  render() {
    return (
      <FlatList
        horizontal={true}
        data={this.trendsArray}
        showsHorizontalScrollIndicator={false}
        renderItem={(trends) => {
          console.log(trends);
          return (
            <Text
              style={{ marginRight: 12, marginTop: 8, color: "gray" }}
              key={trends}
            >
              {trends.item}
            </Text>
          );
        }}
      ></FlatList>
    );
  }
}
