import { StatusBar } from "expo-status-bar";
import React from "react";
import { StyleSheet, Text, View, ScrollView } from "react-native";
import AppBar from "./AppBar";

export default class App extends React.Component {
  render() {
    return (
      <View style={styles.main}>
        <AppBar></AppBar>
        <View>
          <Text style={styles.title}>Trends</Text>
          <ScrollableList></ScrollableList>
        </View>
      </View>
    );
  }
}

class ScrollableList extends React.Component {
  render() {
    return (
      <ScrollView
        style={styles.scrollList}
        horizontal={true}
        showsHorizontalScrollIndicator={false}
      >
        <Text style={([styles.tags], { marginRight: 8 })}># COVID19</Text>
        <Text style={([styles.tags], { marginHorizontal: 8 })}># IPL2020</Text>
        <Text style={([styles.tags], { marginHorizontal: 8 })}>
          # CoronaVaccine
        </Text>
        <Text style={([styles.tags], { marginHorizontal: 8 })}># NEET</Text>
        <Text style={([styles.tags], { marginLeft: 8 })}># Trump</Text>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  main: {
    marginHorizontal: 15,
  },
  title: {
    fontSize: 16,
    fontWeight: "500",
  },
  scrollList: {
    overflow: "scroll",
    paddingVertical: 10,
  },
  tags: {
    borderColor: "gray",
  },
});
